package app.controllers;import app.dtos.LongUrlDto;import app.dtos.ShortUrlDto;import app.services.UrlService;import com.google.inject.Inject;import com.google.inject.Singleton;import io.jooby.annotations.GET;import io.jooby.annotations.POST;import io.jooby.annotations.Path;import io.jooby.annotations.PathParam;import io.reactivex.Observable;@Path("/url")@Singletonpublic class UrlController {    @Inject    public UrlService urlService;    @POST    public Observable<ShortUrlDto> getShortUrl(LongUrlDto longUrlDto) {        return urlService.getShortUrl(longUrlDto.url);    }    @GET    @Path("/{shortUrl}")    public Observable<String> getLongUrl(@PathParam String shortUrl) {        return Observable.just("Url: " + shortUrl);    }}