package app.infrastructure.sequence;import com.google.inject.Inject;import com.google.inject.Singleton;import io.reactivex.Observable;import redis.clients.jedis.JedisPooled;@Singletonpublic class RedisSequenceClient implements SequenceClient {    private final JedisPooled jedisPooled;    @Inject    public RedisSequenceClient(JedisPooled jedisPooled) {        this.jedisPooled = jedisPooled;    }    @Override    public Observable<Long> getId() {        return Observable.create(emitter -> {            try {                long id = this.jedisPooled.incr("id");                emitter.onNext(id);                emitter.onComplete();            } catch (Throwable t) {                emitter.onError(t);            }        });    }}