package app.services;import app.dtos.ShortUrlDto;import com.google.inject.Inject;import com.google.inject.Singleton;import io.reactivex.Observable;@Singletonpublic class UrlService {    private EncoderService encoderService;    @Inject    public UrlService(EncoderService encoderService) {        this.encoderService = encoderService;    }    public Observable<ShortUrlDto> getShortUrl(String longUrl) {        String code = this.encoderService.convertId(1L);        ShortUrlDto shortUrlDto = new ShortUrlDto();        shortUrlDto.url = code;        return Observable.just(shortUrlDto);    }}