package app.services;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertNotNull;import static org.mockito.Mockito.mock;import static org.mockito.Mockito.when;import app.dtos.ShortUrlDto;import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;public class UrlServiceTest {    private EncoderService encoderService;    private UrlService urlService;    @BeforeEach    void setUp() {        this.encoderService = mock(EncoderService.class);        this.urlService = new UrlService(this.encoderService);    }    @Test    public void get_short_url() {        when(this.encoderService.convertId(1L)).thenReturn("abc");        ShortUrlDto actual = this.urlService.getShortUrl("https://www.contoso.com").blockingFirst();        assertNotNull(actual);        assertNotNull(actual.url);        assertEquals("abc", actual.url);    }}